// Recebe os itens de entrada do nó anterior
const items = $input.all();

// Extraindo os IDs de cada item e concatenando em uma string separada por vírgulas
const children = items.map(item => item.json.id).join(',');

// Retorna o resultado
return [
  {
    json: {
      children: children
    }
  }
];
