={
  "query": "mutation CreateDocumentMutation($document: DocumentInput!, $signers: [SignerInput!]!, $file: Upload!) { createDocument(document: $document, signers: $signers, file: $file) { id name refusable sortable created_at signatures { public_id name email created_at action { name } link { short_link } user { id name email } } } }",
  "variables": {
    "document": {
      "name": "{{ $('Map Info Doc').item.json['Nome do Documento'] }}",
      "message": "{{ $('Map Info Doc').item.json['Mensagem Quando Enviado'] }}",
      "reminder": "DAILY",
      "sortable": {{ $('Map Info Doc').item.json['Assinatura em Ordem'] }},
      "refusable": {{ $('Map Info Doc').item.json['Permitir Recusa Documento'] }},
      "footer": "LEFT",
      "deadline_at": "{{ $('Limit').item.json['Vencimento Assinatura'].isNotEmpty() == false ? "" : new Date($('Limit').item.json['Vencimento Assinatura']).toISOString() }}",
      "new_signature_style": true,
      "ignore_cpf": false,
      "configs": {
        "notification_finished": {{ $('Map Info Doc').item.json['Enviar Para Assinatura'] }},
        "notification_signed": {{ $('Map Info Doc').item.json['Receber Notificação Quando Assinado'] }}
      },
      "locale": {
        "language": "pt-BR",
        "country": "BR",
        "timezone": "America/Sao_Paulo",
        "date_format": "DD_MM_YYYY"
      }
    },
    "signers": {{ $('Map Info Usuarios').item.json.signers.toJsonString() }}
  }
}

